import React from 'react';
import {connect} from 'react-redux';
import {createStructuredSelector} from 'reselect';

import {selectShopCollections} from '../../redux/shop/shop.selectors.js';
import CollectionItem from '../../components/collection-item/collection-item.component';

import './category.styles.scss';

const Category = ({match, collections}) => {
  const collection = collections.filter(collection => collection.routeName === match.params.categoryId);
  console.log(collection);
  return(
  <div className='collection-page'>
    <h1 className='title'>{collection.title}</h1>
    {
      collection.map(item => (<CollectionItem key={item.id} item={item}/>))
    }
  </div>
  )
};

const mapStateToProps = createStructuredSelector({
  collections: selectShopCollections
})

export default connect(mapStateToProps)(Category);
