import React from 'react';
import {Link} from 'react-router-dom';
import {connect} from 'react-redux';
import {createStructuredSelector} from 'reselect';

import {auth} from '../../firebase/firebase.utils.js';
import CartIcon from '../cart-icon/cart-icon.component';
import CartDropdown from '../cart-dropdown/cart-dropdown.component';
import {selectCurrentUser} from '../../redux/user/user.selectors.js';
import {selectCartHidden} from '../../redux/cart/cart.selectors.js';

import {ReactComponent as Logo} from '../../assets/crown.svg';

import {HeaderContainer, LogoContainer, OptionsContainer, OptionContainerLink, OptionContainerDiv} from './header.styles'

const Header = ({currentUser, hidden}) => (
  <HeaderContainer>
    <LogoContainer to='/'>
      <Logo className='logo'/>
    </LogoContainer>
    <OptionsContainer>
      <OptionContainerLink to='/shop'>
        SHOP
      </OptionContainerLink>
      <OptionContainerLink to='/contact'>
        CONTACT
      </OptionContainerLink>
      {
      currentUser ?
      <OptionContainerDiv onClick={() => auth.signOut()}>
        SIGN OUT
      </OptionContainerDiv> :
      <OptionContainerLink to='/signin'>
        SIGN IN
      </OptionContainerLink>
      }
      <CartIcon/>
    </OptionsContainer>
    {
      hidden ? null :
      <CartDropdown/>
    }
  </HeaderContainer>
);

const mapStateToProps = state => createStructuredSelector({
  currentUser: selectCurrentUser,
  hidden: selectCartHidden
});

export default connect(mapStateToProps)(Header);
